    public string GetAIName()
    {
        return BiniLab.Utils.RandomPick(this.names);
    }

    public bool CanUseWord(string word)
    {
        return !this.badNames.Exists(n => !string.IsNullOrEmpty(n) && (n.IndexOf(word) >= 0 || word.IndexOf(n) >= 0));
    }

    public Font GetFont()
    {
        switch (this.lan)
        {
            case Language.EN: return this.fontEN;
            case Language.KR: return this.fontKR;
            case Language.JP: return this.fontJP;
            case Language.TC: return this.fontCN_SC;
            case Language.SC: return this.fontCN_SC;
            case Language.TH: return this.fontTH;
            // case Language.RU: return this.fontRU;
        }

        return this.fontArial;
    }

    public TMP_FontAsset GetTextMeshProFont()
    {
        switch (this.lan)
        {
            case Language.EN: return this.fontEN_TxtMeshPro;
            case Language.KR: return this.fontKR_TxtMeshPro;
            case Language.JP: return this.fontJP_TxtMeshPro;
            case Language.TC: return this.fontCN_SC_TxtMeshPro;
            case Language.SC: return this.fontCN_SC_TxtMeshPro;
            case Language.TH: return this.fontTH_TxtMeshPro;
            // case Language.RU: return this.fontRU_TxtMeshPro;
        }

        return this.fontArial_TxtMeshPro;
    }

    public string GetLanguageNameText(Language lan)
    {
        switch (lan)
        {
            case Language.EN: return "ENGLISH";
            case Language.KR: return "한국어";
            case Language.JP: return "日本語";
            case Language.TC: return "繁體中文";
            case Language.SC: return "简体中文";
            case Language.FR: return "Français";
            case Language.DE: return "Deutsch";
            case Language.RU: return "Pусский";
            case Language.ES: return "Español";
            case Language.IT: return "Italiano";
            case Language.PT: return "Português";
            case Language.VN: return "Tiếng Việt";
            case Language.ID: return "bahasa Indonesia";
            case Language.TH: return "ไทย";
            case Language.TR: return "Türk";
            case Language.HI: return "हिन्दी";
        }

        return string.Empty;
