    public string GetLanguageAudioNameText(Language lan, Language audioLan)
    {
        switch (lan)
        {
            case Language.KR:
            {
                switch (audioLan)
                {
                    case Language.KR: return "한국어";
                    case Language.JP: return "일본어";
                }
            }
                break;
            case Language.TC:
                switch (audioLan)
                {
                    case Language.KR: return "韓國語";
                    case Language.JP: return "日語";
                }

                break;
            case Language.SC:
                switch (audioLan)
                {
                    case Language.KR: return "韩国语";
                    case Language.JP: return "日语";
                }

                break;
            case Language.TH:
                switch (audioLan)
                {
                    case Language.KR: return "เกาหลี";
                    case Language.JP: return "ญี่ปุ่น";
                }

                break;
            default:
                switch (audioLan)
                {
                    case Language.KR: return "Korean";
                    case Language.JP: return "Japanese";
                }

                break;
        }

        return string.Empty;
    }

