    public void SetText(InputField inputFieldText, string text)
    {
        try
        {
            if (inputFieldText == null)
            {
                Debug.LogError("WARNINIG!!!!!!! uiText is null");
                return;
            }

            if (inputFieldText.textComponent.font != null)
            {
                if (inputFieldText.textComponent.font.name != this.GetFont().name)
                    inputFieldText.textComponent.font = this.GetFont();
            }
            else
            {
                inputFieldText.textComponent.font = this.GetFont();
            }

            inputFieldText.text = text;
        }
        catch (Exception e)
        {
            Debug.LogError(e);
        }
    }



