    public string GetLeftTimeString(TimeSpan leftTime, bool usingPostFix = false)
    {
        string returnStr = string.Empty;

        if (LanguageManager.Instance.Language == Language.EN)
        {
            if (leftTime.Days > 0)
            {
                returnStr = string.Format("{0:00}d {1:00}h", leftTime.Days, leftTime.Hours);
            }
            else if (leftTime.Hours > 0)
            {
                returnStr = string.Format("{0:00}h {1:00}m", leftTime.Hours, leftTime.Minutes);
            }
            else if (leftTime.Minutes == 0)
            {
                returnStr = string.Format("{0:00}s", leftTime.Seconds);
            }
            else
            {
                returnStr = string.Format("{0:00}m {1:00}s", leftTime.Minutes, leftTime.Seconds);
            }
        }
        else if (LanguageManager.Instance.Language == Language.KR)
        {
            if (leftTime.Days > 0)
            {
                returnStr = string.Format("{0:00}일 {1:00}시간", leftTime.Days, leftTime.Hours);
            }
            else if (leftTime.Hours > 0)
            {
                returnStr = string.Format("{0:00}시간 {1:00}분", leftTime.Hours, leftTime.Minutes);
            }
            else if (leftTime.Minutes == 0)
            {
                returnStr = string.Format("{0:00}초", leftTime.Seconds);
            }
            else
            {
                returnStr = string.Format("{0:00}분 {1:00}초", leftTime.Minutes, leftTime.Seconds);
            }
        }
        else if (LanguageManager.Instance.Language == Language.TC)
        {
            //10天4小時30分鐘20秒
            if (leftTime.Days > 0)
            {
                returnStr = string.Format("{0:00}天 {1:00}小時", leftTime.Days, leftTime.Hours);
            }
            else if (leftTime.Hours > 0)
            {
                returnStr = string.Format("{0:00}小時 {1:00}分", leftTime.Hours, leftTime.Minutes);
            }
            else if (leftTime.Minutes == 0)
            {
                returnStr = string.Format("{0:00}秒", leftTime.Seconds);
            }
            else
            {
                returnStr = string.Format("{0:00}分 {1:00}秒", leftTime.Minutes, leftTime.Seconds);
            }
        }
        else if (LanguageManager.Instance.Language == Language.SC)
        {
            //10天4小时30分钟20秒
            if (leftTime.Days > 0)
            {
                returnStr = string.Format("{0:00}天 {1:00}小时", leftTime.Days, leftTime.Hours);
            }
            else if (leftTime.Hours > 0)
            {
                returnStr = string.Format("{0:00}小时 {1:00}分", leftTime.Hours, leftTime.Minutes);
            }
            else if (leftTime.Minutes == 0)
            {
                returnStr = string.Format("{0:00}秒", leftTime.Seconds);
            }
            else
            {
                returnStr = string.Format("{0:00}分 {1:00}秒", leftTime.Minutes, leftTime.Seconds);
            }
        }
        else if (LanguageManager.Instance.Language == Language.JP)
        {
            //10日4時間30分20秒
            if (leftTime.Days > 0)
            {
                returnStr = string.Format("{0:00}日 {1:00}時間", leftTime.Days, leftTime.Hours);
            }
            else if (leftTime.Hours > 0)
            {
                returnStr = string.Format("{0:00}時間 {1:00}分", leftTime.Hours, leftTime.Minutes);
            }
            else if (leftTime.Minutes == 0)
            {
                returnStr = string.Format("{0:00}秒", leftTime.Seconds);
            }
            else
            {
                returnStr = string.Format("{0:00}分 {1:00}秒", leftTime.Minutes, leftTime.Seconds);
            }
        }
        else
        {
            if (leftTime.Days > 0)
            {
                returnStr = string.Format("{0:00}d {1:00}h", leftTime.Days, leftTime.Hours);
            }
            else if (leftTime.Hours > 0)
            {
                returnStr = string.Format("{0:00}h {1:00}m", leftTime.Hours, leftTime.Minutes);
            }
            else if (leftTime.Minutes == 0)
            {
                returnStr = string.Format("{0:00}s", leftTime.Seconds);
            }
            else
            {
                returnStr = string.Format("{0:00}m {1:00}s", leftTime.Minutes, leftTime.Seconds);
            }
        }

        //if (usingPostFix) returnStr += $" {LanguageManager.Instance.GetText("COMMON_LEFT").ToUpper()}";

        return returnStr;
    }

